mapper.select:
SELECT c.*, p.category_name AS parent_category_name
FROM Category c LEFT JOIN Category p ON c.parent_category_id = p.category_id
WHERE c.created_by = #{userId}

SELECT c.*, p.category_name AS parent_category_name FROM category c LEFT JOIN category p ON c.parent_category_id = p.category_id
WHERE c.category_name LIKE CONCAT('%', #{keyword}, '%') OR c.description LIKE CONCAT('%', #{keyword}, '%')
WHERE c.created_by = #{userId} LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM category

    WHERE category_name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%')

WHERE created_by = #{userId}
